package unisa.gps.etour.control.GestioneRicerche;
import java.rmi.Remote; import java.rmi.RemoteException; import unisa.gps.etour.bean.BeanBeneCulturale; import unisa.gps.etour.bean.BeanPuntoDiRistoro; import unisa.gps.etour.util.Punto3D;
public interface IRicerca extends Remote
/** * Interface for management of research * * @Author Joseph Penna * @Version 0.1 (C) 2007 eTour Project - Copyright by DMI SE @ SA Lab - * University of Salerno */ public interface IRicerca extends Remote {
// Constants identify the types of site public final static byte BENE_CULTURALE = 0; public final static byte PUNTO_DI_RISTORO = 1;
/** * Initialization method for research * * @param pIdTurista ID of the tourist. Pass -1 in case of a Guest * @param pParoleChiave Together keyword search * @param pTagsId Together Search Tags * @param pRaggioMax Maximum distance between the user and the site * @param numeroElementiPerPagina number of items to look for in a * Search session * @param pPosizioneUtente detected by the GPS user position * @param pTipologiaSito type site search * @return number of elements emerged from the research. On error * Returns -1 * @throws RemoteException the remote exception */
public int search(int pIdTurista, String pParoleChiave, int[] pTagsId, double pRaggioMax, int pElementiPerPagina, Punto3D pPosizioneUtente, byte pTipologiaSito) throws RemoteException
/** * Method for returning the list of emerging from the Cultural Heritage * Research, in a given interval * * @param pPagina range of items to be included in the results * @return entirety of cultural property related to the range of results * Selected search. In case of error returns null * @throws RemoteException Exception Remote */
public BeanBeneCulturale[] ottieniPaginaRisultatiBeneCulturale(int pPagina) throws RemoteException
