public SchedaTurista(Turisti pParent, BeanTurista pTurista,
boolean pModifica) {
super();
this.parent = pParent;
setIconifiable(true);
setSize(560, 520);
suggerimenti = new Vector<JLabel>();
setDefaultCloseOperation(WindowConstants.DO_NOTHING_ON_CLOSE);
setClosable(true);
