jContentPane,
root,
"Commit Changes tourist figures",
JOptionPane.YES_NO_OPTION,
JOptionPane.QUESTION_MESSAGE,
new ImageIcon(
getClass().getResource(
"/unisa/gps/etour/gui/operatoreagenzia/images/ModificaTurista48.png")),
